const ProfileCard = styled(Link)`
text-decoration:none;
display: flex;
flex-direction: row;
align-items: center;
margin: 10px auto;
font-weight: 600;
background: ${BLUE}10;
width: 90%;
padding: 10px;
border-radius: 10px;
>svg, >img {
    color: ${props=>props.selected?BLUE:LIGHTGREY};
    height: 3em;
    width: 3em;
    border-radius: 50%;
    object-fit: cover;
}

>div {
    display: flex;
    flex-direction: column;
    margin-left: 10px;
    align-items: flex-start;
    overflow:hidden;

    >p:first-child {
        font-size: 1.1rem;
        text-align: left;
        color: ${GREY}

    }

    >p:last-child {
        font-size: 0.9rem;
        font-weight: 400;
        color: ${GREY};
        text-overflow: ellipsis;

    }
}
`

const MenuContainer = styled.div`
display: flex;
border: ${borderStyle};
flex-direction: column;
justify-content: flex-start;
align-items: center;
background: white;
position: relative;
width: 20vw;
padding-top: 3%;
height: 100%;
overflow: hidden;
border-radius: 2vw;
z-index: 3;
grid-gap: 10px;
@media (max-width: 600px) and (orientation: portrait){
    height: 100vh;
    border-radius: 0;
    top: 0;
    width: 15vw;
    position: sticky;
}
@media ${device.mobileLargePortrait}, ${device.tabletSmallPortrait}{
    position: fixed;
    transition: 0.2s;
    transform: ${props=>props.openMenu?"translateX(0)":"translateX(-120%)"};
    top:5vw;
    left: 5vw;
    width: 60vw;
    height: calc(100vh - 10vw);
    border-radius: 20px;
    padding-top: 10%;

}

@media ${device.tabletSmallPortrait}{
    width: 30vw;

}
`

